import React, { Component } from 'react'
import { Card } from 'react-bootstrap'
var userInfo;
export default class JobApplications extends Component {
constructor(props)
{
    super(props)
    userInfo=JSON.parse(localStorage.getItem("userInfo"));
    this.getAllJobApplications()
    this.state={applications:[],
     base64ContentString :""
  }
}
clickListener=(index)=>{
    try{

        let address=this.state.applications[index].cand_cv_link
        console.log(address)
        const data={
            "address":address
        }
        const options={
            method:"POST",
            headers:{
                'Content-type':"application/json"
                
            },
            body:JSON.stringify(data)
        }
    
        fetch("/fetchFile",options).then(response=>{
            return response.json();
      }).then(data=>{
          let status=data.status;
        
     
          if(status==='Success')
          {
           this.setState({
            base64ContentString:data.result
           })
            
           
    
         
            
           // history.push("");
          }else{
    
           
            console.log("Operation Failed!")
          }
        // `data` is the parsed version of the JSON returned from the above endpoint.
          // { "userId": 1, "id": 1, "title": "...", "body": "..." }
      }).catch((error) => {
        
        //   reqInProcess=reqInProcess+1;
        // this.RequestToServer(reqInProcess);
       console.log("Unexpected error Try again Retrying...  "+error);
      
       
     });
    
    
    }catch(error)
    {
        window.alert("Something is not right. try again")
    }
}
getApplicationsList=function()
{
let data= this.state.applications;
    
    return data.map((key,index)=>{
        return  <li style={{listStyle:"none"}}>

<Card className="JobAdsCard">
            <div className="historymiddlediv">
           
                <text id ="jobTitle" className="rideHistoryAddress">{data[index].cand_name}</text>
         
                <text 
                style={{cursor:"pointer"}}
                onClick={()=>{
                    this.clickListener(index)
                }}
                
                id ="endDate" className="moveToRight">
                    <span
                     style={{marginRight:"5px",color:"blue"}}>
                    view CV
                        </span>
                        
                </text>
         
         <br></br>
         <text  style={{    marginLeft: "12px"}}>
                    <span style={{marginRight:"10px",color:"green",    fontSize: "12px"}}>
Job ID
                        </span>
                        <span style={{    fontSize: "12px"}}>
                        {data[index].job_id}
                        </span>
                </text>
                <br></br>
         <text  style={{    marginLeft: "12px"}}>
                    <span style={{marginRight:"10px",color:"green",    fontSize: "12px"}}>
Email
                        </span>
                        <span style={{    fontSize: "12px"}}>
                        {data[index].cand_email}
                        </span>
                </text>
               
                </div>
                
               
                <div>
                <text  style={{    marginLeft: "12px"}}>
                    <span style={{marginRight:"10px",color:"green",    fontSize: "12px"}}>
Contact Number
                        </span>
                        <span style={{    fontSize: "12px"}}>
{data[index].cand_phone}
                        </span>
                </text>
                <br></br>
                <br></br>
                 </div>
            </Card>
          

            </li>
    });
}
getAllJobApplications=()=>{

try{

    const data={
        "company":userInfo["userData"].company
    }
    const options={
        method:"POST",
        headers:{
            'Content-type':"application/json"
            
        },
        body:JSON.stringify(data)
    }

    fetch("/getAllJobApplications",options).then(response=>{
        return response.json();
  }).then(data=>{
      let status=data.status;
    
 
      if(status==='Success')
      {
       
        
       this.setState({
           applications:data.result
       })

     
        
       // history.push("");
      }else{

       
        console.log("Operation Failed!")
      }
    // `data` is the parsed version of the JSON returned from the above endpoint.
      // { "userId": 1, "id": 1, "title": "...", "body": "..." }
  }).catch((error) => {
    
    //   reqInProcess=reqInProcess+1;
    // this.RequestToServer(reqInProcess);
   console.log("Unexpected error Try again Retrying...  "+error);
  
   
 });


}catch(error)
{
    window.alert("Something is not right. try again")
}

   




}


  render() {
   
        return (
            <div>
                <embed src={`data:application/pdf;base64,${this.state.base64ContentString}`}  type="application/pdf" width="100%"></embed>
                <ul>
                {this.getApplicationsList()}
                </ul>
            </div>
        )
    }
}
